INSERT INTO `bookstore`.`products` (`id`,`name`,`picture`,`description`,`price`,`quanity`,`author_id`) VALUES (0, 'Tìm Em Nơi Anh - Find Me', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Ftim%20em%20noi%20anh.jpg?alt=media&token=56439706-ff5f-4333-9056-e9b3d4fc5372', 'Đây là cuốn tiếp theo sau \"Gọi em bằng tên anh\" (Call me by your name). Những nhân vật gặp lại, và câu chuyện nhiều năm sau, khi họ đều đã lớn tuổi hơn và có những ngã rẽ riêng trong cuộc đời mình.\r\nTác giả chia sẻ thêm về quyết định viết tiếp câu chuyện:', 87200.00, NULL, 18),(1, 'Nhà Giả Kim (Tái Bản 2020)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fnha_gia_kim.jpg?alt=media&token=685ccefe-8fc4-4c74-a08b-ed4d5400bae5', 'Tất cả những trải nghiệm trong chuyến phiêu du theo đuổi vận mệnh của mình đã giúp Santiago thấu hiểu được ý nghĩa sâu xa nhất của hạnh phúc, hòa hợp với vũ trụ và con người. \r\n\r\nTiểu thuyết Nhà giả kim của Paulo Coelho như một câu chuyện cổ tích giản dị,', 67150.00, NULL, 1),(2, 'Cây Cam Ngọt Của Tôi', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fcay_cam_ngot_cua_toi.jpg?alt=media&token=76128e8f-acd9-4add-a634-0220c27f5567', 'Vị chua chát của cái nghèo hòa trộn với vị ngọt ngào khi khám phá ra những điều khiến cuộc đời này đáng sống... một tác phẩm kinh điển của Brazil.” - Booklist\r\n\r\n“Một cách nhìn cuộc sống gần như hoàn chỉnh từ con mắt trẻ thơ… có sức mạnh sưởi ấm và làm ta', 91800.00, NULL, 2),(3, 'Bước Chậm Lại Giữa Thế Gian Vội Vã (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fbuoc_cham_lai_giua_the_gian_voi_va.jpg?alt=media&token=04ec1e26-7402-49f2-ba9d-134fa3d81e3f', 'Bước Chậm Lại Giữa Thế Gian Vội Vã (Tái Bản 2018)\r\n\r\nChen vai thích cánh để có một chỗ bám trên xe buýt giờ đi làm, nhích từng xentimét bánh xe trên đường lúc tan sở, quay cuồng với thi cử và tiến độ công việc, lu bù vướng mắc trong những mối quan hệ cả t', 68000.00, NULL, 3),(4, 'Bố Già (Đông A)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fbo%20gia.jpg?alt=media&token=5e3d32dd-feed-44f1-a861-ff956a67d062', 'Bố Già (Đông A)\r\n\r\nThế giới ngầm được phản ánh trong tiểu thuyết Bố già là sự gặp gỡ giữa một bên là ý chí cương cường và nền tảng gia tộc chặt chẽ theo truyền thống mafia xứ Sicily với một bên là xã hội Mỹ nhập nhằng đen trắng, mảnh đất màu mỡ cho những ', 71500.00, NULL, 4),(5, 'Con Chim Xanh Biếc Bay Về - Tặng Kèm 6 Postcard', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fcon%20chim%20xanh%20biec%20bay%20ve.jpg?alt=media&token=0347d6e2-beba-4796-b801-4b0d1a7b33b9', 'Con Chim Xanh Biếc Bay Về\r\n\r\nKhông giống như những tác phẩm trước đây lấy bối cảnh vùng quê miền Trung đầy ắp những hoài niệm tuổi thơ dung dị, trong trẻo với các nhân vật ở độ tuổi dậy thì, trong quyển sách mới lần này nhà văn Nguyễn Nhật Ánh lấy bối cản', 112500.00, NULL, 5),(6, 'Dấu Chân Trên Cát (Tái Bản 2020)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fdau%20chan%20tren%20cat.jpg?alt=media&token=3bae80ce-d35c-431e-8f17-efb3a7793f51', '“Dấu chân trên cát” là tác phẩm được dịch giả Nguyên Phong phóng tác kể về xã hội Ai Cập thế kỷ thứ XIV trước CN, qua lời kể của nhân vật chính -  Sinuhe.\r\n\r\nNgày nay, người ta biết đến triều đại các vua chúa Ai Cập thời cổ qua sách vở của người Hy Lạp.', 100640.00, NULL, 6),(7, 'Chiến Binh Cầu Vồng (Tái Bản 2020)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fchien%20binh%20cau%20vong.jpg?alt=media&token=8695de7f-c6cb-4f3b-9fb3-ac8ba98276e9', 'Một tác phẩm có tầm ảnh hưởng sâu rộng nhất Indonesia\r\n\r\n“Thầy Harfan và cô Mus nghèo khổ đã mang đến cho tôi tuổi thơ đẹp nhất, tình bạn đẹp nhất, và tâm hồn phong phú, một thứ gì đó vô giá, thậm chí còn có giá trị hơn những khao khát mơ ước. Có thể tôi ', 92650.00, NULL, 7),(8, 'Cho Tôi Xin Một Vé Đi Tuổi Thơ (Bìa Mềm) (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fcho%20toi%20xin%201%20ve%20di%20tuoi%20th%C6%A1.jpg?alt=media&token=d3f574bc-c0ed-4535-b01b-1daa12b95753', 'Trong Cho tôi xin một vé đi tuổi thơ, nhà văn Nguyễn Nhật Ánh mời người đọc lên chuyến tàu quay ngược trở lại thăm tuổi thơ và tình bạn dễ thương của 4 bạn nhỏ. Những trò chơi dễ thương thời bé, tính cách thật thà, thẳng thắn một cách thông minh và dại dộ', 64000.00, NULL, 5),(9, 'Tôi Là Bêtô (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Ftoi%20la%20beto.jpg?alt=media&token=2b509c92-0c83-4b07-9221-60500cbebfc0', 'Truyện Tôi là Bêtô là sáng tác mới nhất của nhà văn Nguyễn Nhật Ánh được viết theo phong cách hoàn toàn khác so với những tác phẩm trước đây của ông. Những mẩu chuyện, hay những phát hiện của chú chó Bêtô đầy thú vị, vừa hài hước, vừa chiêm nghiệm một các', 72250.00, NULL, 5),(10, 'Cảm Ơn Người Lớn (Bìa Mềm)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fcam%20on%20nguoi%20lon.jpg?alt=media&token=dfa6afc3-2f07-4149-9ae3-758cd539fb89', 'Cảm Ơn Người Lớn (Bìa Mềm)\r\n\r\nCảm ơn người lớn - một áng văn lãng mạn trong giọng hài hước đặc biệt “dành cho trẻ em, và những ai từng là trẻ em”..\r\n\r\nBạn sẽ gặp lại Mùi, Tủn, Tí sún, Hải cò… của Cho tôi xin một vé đi tuổi thơ, cùng chơi những trò chơi qu', 88000.00, NULL, 5),(11, 'Bàn Có 5 Chỗ Ngồi (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fban%20co%205%20cho%20ngoi.jpg?alt=media&token=2a12f6de-9ad0-4ccd-ae06-0d169f06fddb', 'Tác giả quen thuộc của thiếu nhi, của tuổi ô mai. Những tập truyện của anh luôn dí dỏm, hài hước, đem lại nhiều cảm xúc, nhiều bài học nhẹ nhàng cho lứa tuổi học trò. Hãy đọc để cùng gặp những nhân vật rất dễ thương, hoặc phải vượt qua những hoàn cảnh khó', 44550.00, NULL, 5),(12, 'Trường Ca Achilles', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Ftruong%20ca.jpg?alt=media&token=6bfcb27a-dca1-4966-b5f5-00bbef057962', 'HUYỀN THOẠI BẮT ĐẦU…\r\n\r\nHy Lạp vào thời hoàng kim của các anh hùng. Patroclus là một hoàng tử trẻ vụng về, bị trục xuất tới vương quốc Phthia và được nuôi dạy dưới sự che chở của vua Peleus cùng cậu con trai hoàng kim của ngài, Achilles. “Người Hy Lạp vĩ ', 148200.00, NULL, 8),(14, 'Chuyện Con Ốc Sên Muốn Biết Tại Sao Nó Chậm Chạp (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fchuyen%20con%20oc%20sen%20muon%20biet%20tai%20sao%20no%20cham%20cham.jpg?alt=media&token=7b1f75ad-ff71-4b53-8f57-1e3f88a6218e', 'Đây là câu chuyện về một bầy ốc sên sống tại Quê Hương Bồ Công Anh, dưới tán lá ô rô rậm rạp, vốn vẫn sống một cuộc đời chậm chạp, lặng lẽ, và chỉ gọi nhau đơn giản là “sên”, cho tới khi một con trong số chúng nghĩ rằng thật bất công khi không có một cái ', 33150.00, NULL, 10),(15, '999 Lá Thư Gửi Cho Chính Mình - Mong Bạn Trở Thành Phiên Bản Hoàn Hảo Nhất (Tập 1) - Tái Bản 2021', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2F999%20la%20thu.jpg?alt=media&token=3fd8b810-1802-445d-9b4e-616e916be008', '“999 lá thư gửi cho chính mình” là một tác phẩm đặc biệt đầy cảm hứng đến từ tác giả văn học mạng nổi tiếng Miêu Công Tử, mang một màu sắc riêng biệt qua những lời thư nhỏ nhắn nhủ đến người đọc về giá trị cuộc sống, tình yêu, tuổi trẻ, tương lai… v.v.. đ', 105780.00, NULL, 11),(16, 'Có Hai Con Mèo Ngồi Bên Cửa Sổ (Tái Bản 2018)', 'https://firebasestorage.googleapis.com/v0/b/image-c757c.appspot.com/o/tieuthuyet%2Fco%202%20con%20meo%20ngoi%20ben%20cua%20so.jpg?alt=media&token=2caceff7-3c19-40c6-a546-7bdcde57f1ba', 'Có Hai Con Mèo Ngồi Bên Cửa Sổ (Tái Bản 2018)\r\n\r\nÓ HAI CON MÈO NGỒI BÊN CỬA SỔ là tác phẩm đầu tiên của nhà văn Nguyễn Nhật Ánh viết theo thể loại đồng thoại. Đặc biệt hơn nữa là viết về tình bạn c